import Card from "./Card";
import "./styles.css";

// import useWindowSize from 'react-use/lib/useWindowSize'
import Confetti from 'react-confetti'

export const confetti =  () => {
  return (
    <Confetti
      width={window.width}
      height={window.height}
    />
  )
}


export default function App() {
  return (
    <div className="App">
      <Card onHover={confetti}/>

    </div>
  );
}
